/**
 * Login user and store access token in cookie
 */
export async function login(data: LoginData): Promise<{ user: User; accessToken: string }> {
    console.log('[AuthAPI] Initiating login request...');
    const response = await apiClient.post<AuthResponse>('/api/auth/login', data);
    const apiResponse = response.data;

    // Debug nested structure
    console.log('[AuthAPI] Login response structure:', JSON.stringify({
        hasData: !!apiResponse.data,
        hasTokens: !!apiResponse.data?.tokens,
        hasAccessToken: !!apiResponse.data?.tokens?.access?.token
    }));

    // Extract token and user from nested data structure
    const accessToken = apiResponse.data?.tokens?.access?.token || (apiResponse as any).accessToken;
    const user = apiResponse.data?.user || (apiResponse as any).user;

    if (!accessToken || !user) {
        throw new Error('Invalid response from server: Access token or user missing');
    }

    setAuthCookie(accessToken);
    return { user, accessToken };
}

/**
 * Register new user
 */
export async function register(data: RegisterData): Promise<{ user: User; accessToken: string }> {
    const response = await apiClient.post<AuthResponse>('/api/auth/register', data);
    const apiResponse = response.data;

    const accessToken = apiResponse.data?.tokens?.access?.token || (apiResponse as any).accessToken;
    const user = apiResponse.data?.user || (apiResponse as any).user;

    if (!accessToken || !user) {
        console.error('[AuthAPI] Failed to extract tokens or user. Response:', JSON.stringify(apiResponse));
        throw new Error('Invalid response from server: Access token or user missing');
    }

    setAuthCookie(accessToken);
    console.log(`[AuthAPI] Session established. Token saved to storage.`);
    return { user, accessToken };
}

/**
 * Logout user
 */
export async function logout(): Promise<void> {
    try {
        await apiClient.post('/api/auth/logout');
    } finally {
        deleteAuthCookie();
    }
}

/**
 * Get current user profile
 */
export async function getCurrentUser(): Promise<User> {
    const response = await apiClient.get<any>('/api/auth/me');
    // Handle both { data: { user } } and { data: user }
    const userData = response.data.data || response.data.user || response.data;

    if (!userData || typeof userData !== 'object') {
        throw new Error('Invalid user data received from server');
    }

    return userData;
}

// Cookie utilities for auth tokens
export const AUTH_COOKIE_NAME = 'auth_token_frontend'

/**
 * Set the auth token in cookie (for middleware).
 */
export function setAuthCookie(token: string, expiresInMs: number = 7 * 24 * 60 * 60 * 1000) {
  if (typeof window === 'undefined') return;

  const date = new Date();
  date.setTime(date.getTime() + expiresInMs);
  const expires = "; expires=" + date.toUTCString();

  // Set the cookie using a unique name
  document.cookie = `${AUTH_COOKIE_NAME}=${token}${expires}; path=/; SameSite=Lax`;

  console.log(`[Storage] Token saved to cookie. Len: ${token.length}`);
}

export function getAuthCookie(): string | null {
  if (typeof document === 'undefined') return null;

  const nameEQ = AUTH_COOKIE_NAME + "=";
  const ca = document.cookie.split(';');
  for (let i = 0; i < ca.length; i++) {
    let c = ca[i].trim();
    if (c.indexOf(nameEQ) === 0) {
      const token = c.substring(nameEQ.length, c.length);
      console.log('[Storage] Token recovered from COOKIE');
      return token;
    }
  }

  return null;
}

export function deleteAuthCookie() {
  if (typeof document !== 'undefined') {
    document.cookie = `${AUTH_COOKIE_NAME}=; Path=/; Expires=Thu, 01 Jan 1970 00:00:01 GMT; SameSite=Lax;`;
  }
  console.log(`[Storage] Token cleared from storage`);
}

import { NextResponse } from 'next/server';
import type { NextRequest } from 'next/server';
import { AUTH_COOKIE_NAME } from './lib/cookies';

export function middleware(request: NextRequest) {
    const { pathname } = request.nextUrl;

    // Define public routes that don't require authentication
    const publicRoutes = ['/login', '/signup', '/inventory-public'];
    const isPublicRoute = publicRoutes.some((route) => pathname.startsWith(route));

    // 0. Ignore static files, api routes, and common dev paths
    if (
        pathname.startsWith('/_next') ||
        pathname.startsWith('/api') ||
        pathname.startsWith('/favicon.ico') ||
        pathname.startsWith('/.well-known') ||
        pathname.includes('.') // common for static files like images/manifests
    ) {
        return NextResponse.next();
    }

    // Check for the access token cookie
    const allCookies = request.cookies.getAll();
    const token = request.cookies.get('auth_token_frontend')?.value;

    console.log(`[Middleware] Path: ${pathname}`);
    console.log(`[Middleware] All Cookies: ${allCookies.map(c => `${c.name}=${c.value.substring(0, 10)}...`).join(', ')}`);
    console.log(`[Middleware] Target Cookie '${'auth_token_frontend'}': ${token ? 'FOUND' : 'MISSING'}`);

    // 1. If no token and trying to access a protected route (anything not public)
    const hasRefreshToken = request.cookies.has('refreshToken');
    if (!token && !hasRefreshToken && !isPublicRoute) {
        // Exclude static assets and api routes if necessary, but matcher usually handles this
        console.log(`[Middleware] Unauthorized access to ${pathname}. Redirecting to /login`);
        return NextResponse.redirect(new URL('/login', request.url));
    }

    // 2. If token exists and trying to access login/signup, redirect to dashboard
    if (token && (pathname === '/login' || pathname === '/signup')) {
        console.log(`[Middleware] Redirecting to / from ${pathname}`);
        return NextResponse.redirect(new URL('/', request.url));
    }

    return NextResponse.next();
}

// See "Matching Paths" below to learn more
export const config = {
    matcher: [
        /*
         * Match all request paths except for the ones starting with:
         * - api (API routes)
         * - _next/static (static files)
         * - _next/image (image optimization files)
         * - favicon.ico (favicon file)
         */
        '/((?!api|_next/static|_next/image|favicon.ico).*)',
    ],
};
